boldenCurrentTab=function(tabName){$('.navLink').each(function(idx,elem){$(elem).removeClass('bold')});$('a[data-name="'+tabName+'"]').each(function(idx,elem){$(elem).addClass('bold')})};$(document).ready(function(){$(window).on('slideChange',function(event,number,element){boldenCurrentTab(element.data('name')||'home')});boldenCurrentTab(window.location.hash.substr(1)||'home')});